/*
 parameters
 <NavBar title={}  height={} background={} />
 */

class BottomNavBar extends React.Component {
    constructor(props) {

        super(props);
        let height = parseInt(this.props.height) || 100;
        let lineHeight = parseInt(this.props.height) || 100;
        this.state = {
            height: height,
            lineHeight: lineHeight,
            background: this.props.background || 'white'
        };
    }

    render() {
        let easyloan_style_footer_fixed = {
            width: "720px",
            height: "110px",
            position: "fixed",
            left: "0",
            right: "0",
            bottom: "0",
            background: "#393f5a",
            borderTop: "1px solid #f0f0f0",
            zIndex: "100"
        };
        let isActiveTab = (tab) => {
            let pt = location.pathname;
            return pt.match(`/static/loan/${tab}`)
        }

        const STYLE_TAB_BASE = {
            width: "20%",
            display: "block",
            float: "left",
            textAlign: "center",
            fontSize: "24px",
        }
        const STYLE_ICON_BASE = {
            height: "51px",
            display: "block",
            backgroundRepeat: "no-repeat",
            backgroundPosition: "center",
            margin: "10px 0"
        }

        let _style_tab_a = Object.assign({}, STYLE_TAB_BASE, {
            color: isActiveTab('home') ? '#77a4ea' : "#ffffff"
        })
        let _style_tab_bill = Object.assign({}, STYLE_TAB_BASE, {
            color: isActiveTab('bill') ? '#77a4ea' : "#ffffff"
        })
        let _style_tab_invite = Object.assign({}, STYLE_TAB_BASE, {
            color: isActiveTab('invite') ? '#77a4ea' : "#ffffff"
        })
        let _style_tab_self = Object.assign({}, STYLE_TAB_BASE, {
            color: isActiveTab('user') ? '#77a4ea' : "#ffffff"
        })
        let _style_tab_more = Object.assign({}, STYLE_TAB_BASE, {
            color: isActiveTab('more') ? '#77a4ea' : "#ffffff"
        })
        let _style_tab_a_icon = Object.assign({}, STYLE_ICON_BASE, {
            backgroundImage: isActiveTab('home') ? "url('data:image/png;base64,') " : "url( 'data:image/png;base64,')"
        })
        let _style_tab_bill_icon = Object.assign({}, STYLE_ICON_BASE, {
            backgroundImage: isActiveTab('bill') ? "url('data:image/png;base64,')" : "url('data:image/png;base64,')"
        })
        let _style_tab_invite_icon = Object.assign({}, STYLE_ICON_BASE, {
            backgroundImage: isActiveTab('invite') ? "url('data:image/png;base64,')" : "url('data:image/png;base64,')"
        })
        let _style_tab_self_icon = Object.assign({}, STYLE_ICON_BASE, {
            backgroundImage: isActiveTab('user') ? "url('data:image/png;base64,')" : "url('data:image/png;base64,')"
        })
        let _style_tab_more_icon = Object.assign({}, STYLE_ICON_BASE, {
            backgroundImage: isActiveTab('more') ? "url('data:image/png;base64,')" : "url('data:image/png;base64,')"
        })
        let _href_a = isActiveTab('home') ? null : '/static/loan/home/index.html'
        let _href_bill = isActiveTab('bill') ? null : '/'
        let _href_invite = isActiveTab('invite') ? null : '/'
        let _href_self = isActiveTab('user') ? null : '/static/loan/user/index.html'
        let _href_more = isActiveTab('user-settings') ? null : '/static/loan/user-settings/index.html'
        return (
            <div style={easyloan_style_footer_fixed}>
                <a style={_style_tab_a} href={_href_a}>
                    <i style={_style_tab_a_icon}></i>
                    借钱
                </a>
                <a style={_style_tab_bill} href={_href_bill}>
                    <i style={_style_tab_bill_icon}></i>
                    账单
                </a>
                <a style={_style_tab_invite} href={_href_invite}>
                    <i style={_style_tab_invite_icon}></i>
                    邀友
                </a>
                <a style={_style_tab_self} href={_href_self}>
                    <i style={_style_tab_self_icon}></i>
                    我的
                </a>
                <a style={_style_tab_more} href={_href_more}>
                    <i style={_style_tab_more_icon}></i>
                    更多
                </a>
                <div className="domebox"></div>
            </div>
        )
    }
}
